s=open('I_Malavoglia.txt',"r",encoding="UTF-8").read()for c in "1234567890'?!%&/()=?*+;,:._-><';:\tâ†‘":    s=s.replace(c,' ')s=s.replace('"',' ')while '  ' in s:    s=s.replace('  ',' ')lPar=s.strip().split('\n')  d={}for i in range(len(lPar)):    if len(lPar[i])>1:        d[i]=lPar[i]        for k in d:    v={'a':0,'e':0,'i':0,'o':0,'u':0}    for p in d[k]:        if p[0].lower() in 'aeiou':            v[p[0].lower()]+=1    d[k]=[d[k].strip(),v]f=open('dizionarioParagrafiMalavoglia.txt','w', encoding='utf8')f.write(str(d))dd=eval(open('dizionarioParagrafiMalavoglia.txt','r', encoding='utf8').read())